import './App.css';
import React, {useState, useEffect} from 'react'
import {BrowserRouter, Route, Routes} from "react-router-dom";
import Login from './pages/Login';
import Registration from './pages/Registration';
import AccountPage from './pages/AccountPage';
import CreatePost from './pages/CreatePost';
import FoundPage from './pages/FoundPage';
import HomePage from './pages/HomePage';
import LostPage from './pages/LostPage';
import Header from './partials/Header';
import Footer from './partials/Footer';


function App() {

  const excludeHeaderFooter = ['/login', '/register']; // Add any other paths you want to exclude here

  const shouldRenderHeaderFooter = (path) => {
    return !excludeHeaderFooter.includes(path);
  };

  return (
    <BrowserRouter>
        {shouldRenderHeaderFooter(window.location.pathname) && (
          <Header /> /* Header component */
        )}
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Registration />} />
          <Route path="/" element={<HomePage />} />
        </Routes>
        {shouldRenderHeaderFooter(window.location.pathname) && (
          <Footer /> /* Footer component */
        )}
      </BrowserRouter>
  );
}

export default App;
