import React, {useEffect, useState} from 'react'
import Sidebar from '../partials/Sidebar';
import Service from '../API/Service'


const LostPage = () => {

  const [posts, setPosts] = useState([]);
  const [location, setLocation] = useState("");
  const [animal_species, setSpecies] = useState("");
  const [animal_color, setColor] = useState("");
  const [description, setDescription] = useState("");
  const [animal_gender, setGender] = useState("");

  useEffect(() => {
    const queryParams = [];
    if (location) {
      queryParams.push(`location=${location}`);
    }
    if (animal_species) {
      queryParams.push(`animal_species=${animal_species}`);
    }
    if (animal_color) {
      queryParams.push(`animal_color=${animal_color}`);
    }
    if (description) {
      queryParams.push(`description=${description}`);
    }
    if (animal_gender) {
      queryParams.push(`animal_gender=${animal_gender}`);
    }
  
    const queryString = queryParams.join("&");

    console.log(queryString)
  
    const url = `/posts?${queryString}`;
    fetch(url)
      .then((res) => {res.json(); console.log(res)})
      .then((data) => setPosts(data))
      .catch((err) => console.error(err));
  }, [location, animal_species, animal_color, description, animal_gender]);


  return (
    <div style={{ display: 'flex', height: '100%' }}>
      {/* Sidebar component */}
      <Sidebar/>

      <div style={{ flex: '1', padding: '2rem' }}>
        {/* Page content */}
        <h1>Lost</h1>
        {/* Add other page content here */}
      </div>
    </div>
  )
}

export default LostPage
