import React, {useEffect, useState} from 'react'
import Sidebar from '../partials/Sidebar';
import Service from '../API/Service'


const LostPage = () => {

  const [posts, setPosts] = useState([]);
  const [post_mode, setPostMode] = useState("1");

  const [location, setLocation] = useState("");
  const [animal_species, setSpecies] = useState("");
  const [animal_color, setColor] = useState("");
  const [description, setDescription] = useState("");
  const [animal_gender, setGender] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const query = new URLSearchParams({
        animal_id: animal_species,
        animal_breed: animal_species,
        animal_color: animal_color,
        animal_gender: animal_gender,
        description: description,
        location: location,
      }).toString();

      const response = await fetch(`/posts?${query}`);
      const data = await response.json();

      setPosts(data);
    };

    fetchData();
  }, [location, animal_species, animal_color, description, animal_gender]);


  return (
    <div style={{ display: 'flex', height: '100%' }}>
      {/* Sidebar component */}
      <Sidebar/>

      <div style={{ flex: '1', padding: '2rem' }}>
        {/* Page content */}
        <h1>Lost</h1>
        {posts.map((post) => (
        <div key={post.post_id}>
          <h3>{post.animal_breed}</h3>
          <p>{post.description}</p>
        </div>
      ))}
        {/* Add other page content here */}
      </div>
    </div>
  )
}

export default LostPage
