import React, { useState } from 'react';
import { Container, Form, Button, Tab, Tabs } from 'react-bootstrap';
import PostCard from '../components/PostCard';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';


const AccountPage = () => {
  const [userFirstName, setUserFirstName] = useState('');
  const [userLastName, setUserLastName] = useState('');
  const [userPassword, setUserPassword] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const [userPhone, setUserPhone] = useState('');
  const [activeTab, setActiveTab] = useState('likedPosts'); // State to keep track of active tab

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Perform form submission logic here
  }

  const handleTabSelect = (tabKey) => {
    setActiveTab(tabKey);
  }

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
  };

  return (
    <div>
      <Container style={{marginTop: '64px'}}>
        <h1>Personal Account</h1>
        <Tabs activeKey={activeTab} onSelect={handleTabSelect} style={{marginTop:'32px'}}>
          <Tab eventKey="likedPosts" title="Liked Posts">
            <div style={{marginTop:'64px'}}>
              <h3>Liked posts</h3>
              <Slider {...settings} style={{marginTop:'64px'}}>
                {/* Slide contents */}
              </Slider>
            </div>
          </Tab>
          <Tab eventKey="myPosts" title="My Posts">
            <div style={{marginTop:'64px'}}>
              <h3>My posts</h3>
              <Slider {...settings} style={{marginTop:'64px'}}>
                {/* Slide contents */}
              </Slider>
            </div>
          </Tab>
        </Tabs>

        <Form onSubmit={handleFormSubmit} style={{width:'30%', margin: '0', position: 'fixed', left: '0', top: '0'}}>
        <br />
          <Form.Group controlId="userFirstName">
            <Form.Label>First Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter your first name"
              value={userFirstName}
              onChange={(e) => setUserFirstName(e.target.value)}
            />
          </Form.Group>
          {/* Rest of the form */}
          {/* ... */}
        </Form>
      </Container>
    </div>
  );
}

export default AccountPage;
